const mongoFindOne = async (col, findCondition, selectCondition = {}) => {
  const result = await col.findOne(findCondition).select(selectCondition).lean().exec();
  console.log("===== from the mongo find one " + JSON.stringify(result));
  return result;
};

exports.mongoFindOne = mongoFindOne;

const mongoFindOneWithOutLean = async (col, findCondition, selectCondition = {}) => {
  const result = await col.findOne(findCondition).select(selectCondition).exec();
  return result;
};

exports.mongoFindOneWithOutLean = mongoFindOneWithOutLean;

const mongoFindWithSort = async (col, findCondition, selectCondition = {}, limit = 0, sortCondition = {
  _id: -1
}) => {
  const result = await col.find(findCondition).select(selectCondition).sort(sortCondition).limit(limit).lean().exec();
  return result;
};

exports.mongoFindWithSort = mongoFindWithSort;

const mongoFindMany = async (col, findCondition, selectCondition = {}, limit = 0) => {
  const result = await col.find(findCondition).select(selectCondition).limit(limit).lean().exec();
  return result;
};

exports.mongoFindMany = mongoFindMany;

const mongoFindCursor = async (col, findCondition, selectCondition = {}, limit = 0) => {
  const cursor = await col.find(findCondition).select(selectCondition).limit(limit).lean().cursor();
